import React from 'react';
import PropTypes from 'prop-types';
import 'reset-css/reset.css';
import '../color/color';
import '../grid/grid';
import '../typography/typography';
import classnames from 'classnames';
import css from './badge.css';

const Badge = ({ text, variant, ...otherProps }) => (
  <span
    className={classnames(css['ds-badge'], {
      [css[`ds-badge--${variant}`]]: variant,
    })}
    {...otherProps}
  >
    {text}
  </span>
);

Badge.defaultProps = {
  text: '',
  variant: null,
};

Badge.propTypes = {
  text: PropTypes.string,
  variant: PropTypes.oneOf(['brand', 'sub-brand', 'report']),
};

export default Badge;
