import React from 'react';
import { shallow } from 'enzyme';
import Badge from './badge';

describe('<Badge />', () => {
  const text = 'Badge text';
  it('should render the correct default className', () => {
    const component = shallow(<Badge />);
    expect(component.find('span').props().className).toEqual('ds-badge');
  });

  it('should render the text it is passed', () => {
    const component = shallow(<Badge text={text} />);
    expect(component.text()).toEqual(text);
  });

  it('should render the brand classname if passed brand as a prop', () => {
    const component = shallow(<Badge text={text} variant='brand' />);
    expect(component.find('span').props().className).toEqual(
      'ds-badge ds-badge--brand',
    );
  });

  it('should render the sub-brand classname if passed subBrand as a prop', () => {
    const component = shallow(<Badge text={text} variant='sub-brand' />);
    expect(component.find('span').props().className).toEqual(
      'ds-badge ds-badge--sub-brand',
    );
  });

  it('should render the report classname if passed report as a prop', () => {
    const component = shallow(<Badge text={text} variant='report' />);
    expect(component.find('span').props().className).toEqual(
      'ds-badge ds-badge--report',
    );
  });
});
