import React from 'react';
import PropTypes from 'prop-types';
import classnames from 'classnames';
import 'reset-css/reset.css';
import '../color/color';
import '../color/color-hsl';
import '../grid/grid';
import '../interactions/interactions';
import '../typography/typography';
import css from './call-to-action.css';

const CallToAction = ({ href, text, inverse, secondary, ...otherProps }) => (
  <a
    className={classnames(css['ds-call-to-action'], {
      [css['ds-call-to-action--secondary']]: secondary,
      [css['ds-call-to-action--inverse']]: inverse,
    })}
    href={href}
    {...otherProps}
  >
    {text}
  </a>
);

CallToAction.defaultProps = {
  inverse: false,
  secondary: false,
  href: '',
};

CallToAction.propTypes = {
  href: PropTypes.string,
  text: PropTypes.string.isRequired,
  inverse: PropTypes.bool,
  secondary: PropTypes.bool,
};

export default CallToAction;
