import React from 'react';
import PropTypes from 'prop-types';
import classnames from 'classnames';
import css from './sub-headline.css';

const SubHeadline = ({
  as: Component,
  children,
  className,
  tagline,
  ...otherProps
}) => (
  <Component
    className={classnames([css['ds-card-subheadline'], className])}
    {...otherProps}
  >
    {tagline && (
      <>
        <span className={css['ds-card-subheadline__tagline']}>{tagline}</span> |{' '}
      </>
    )}
    {children}
  </Component>
);

SubHeadline.defaultProps = {
  as: 'p',
  children: null,
  className: '',
  tagline: '',
};

SubHeadline.propTypes = {
  as: PropTypes.oneOf(['h1', 'h2', 'h3', 'h4', 'h5', 'h6', 'p', 'span']),
  children: PropTypes.node,
  className: PropTypes.string,
  tagline: PropTypes.string,
};

export default SubHeadline;
