import React from 'react';
import { shallow } from 'enzyme';
import ControlDragHandle from './control-drag-handle';

describe('<ControlDragHandle />', () => {
  it('should render the correct default props', () => {
    const component = shallow(<ControlDragHandle />);
    expect(component.props().className).toEqual('ds-control-drag-handle');
  });

  it('should render correct disabled attributes if passed', () => {
    const component = shallow(<ControlDragHandle disabled />);
    expect(component.props().className).toEqual(
      'ds-control-drag-handle ds-control-drag-handle--disabled',
    );
    expect(component.find('ControlDrag').props().disabled).toEqual(true);
  });

  it('should render correct inverse attributes if passed', () => {
    const component = shallow(<ControlDragHandle inverse />);
    expect(component.props().className).toEqual(
      'ds-control-drag-handle ds-control-drag-handle--inverse',
    );
    expect(component.find('ControlDrag').props().inverse).toEqual(true);
  });

  it('should render correct horizontal attributes if passed', () => {
    const component = shallow(<ControlDragHandle horizontal />);
    expect(component.props().className).toEqual(
      'ds-control-drag-handle ds-control-drag-handle--horizontal',
    );
    expect(component.find('ControlDrag').props().horizontal).toEqual(true);
  });
});
