import React from 'react';
import { shallow } from 'enzyme';
import ControlLabel from './control-label';

describe('<ControlLabel />', () => {
  const text = 'Label text';
  it('should render the correct default className', () => {
    const component = shallow(<ControlLabel />);
    expect(component.find('div').props().className).toEqual('ds-control-label');
  });

  it('should render the inverse className', () => {
    const component = shallow(<ControlLabel inverse />);
    expect(component.find('div').props().className).toEqual(
      'ds-control-label ds-control-label--inverse',
    );
  });

  it('should render the text it is passed', () => {
    const component = shallow(<ControlLabel text={text} />);
    expect(component.text()).toEqual(text);
  });
});
