import React from 'react';
import { shallow } from 'enzyme';
import FormCheckLabel from './form-check-label';

describe('<FormCheckLabel />', () => {
  it('should render a label with default class name', () => {
    const component = shallow(
      <FormCheckLabel>Form check label</FormCheckLabel>,
    );
    expect(component.find('label').props().className).toEqual(
      'ds-form-check-label',
    );
  });

  it('should render the children', () => {
    const component = shallow(
      <FormCheckLabel>Form check label</FormCheckLabel>,
    );
    expect(component.find('label').props().children).toEqual(
      'Form check label',
    );
  });

  it('should render disabled class name if isDisabled prop is passed', () => {
    const component = shallow(
      <FormCheckLabel isDisabled>Form check label</FormCheckLabel>,
    );
    expect(component.find('label').props().className).toEqual(
      'ds-form-check-label ds-form-check-label--disabled',
    );
  });
});
