import React from 'react';
import { shallow } from 'enzyme';
import FormHelperText from './form-helper-text';

describe('<FormHelperText />', () => {
  it('should render the correct default class', () => {
    const component = shallow(<FormHelperText text='Helper text' />);
    expect(component.find('p').props().className).toEqual(
      'ds-form-helper-text',
    );
  });

  it('should render the text', () => {
    const component = shallow(<FormHelperText text='Helper text' />);
    expect(component.find('p').text()).toEqual('Helper text');
  });

  it('should render inverse classname if inverse prop is passed', () => {
    const component = shallow(<FormHelperText inverse text='Helper text' />);
    expect(component.find('p').props().className).toEqual(
      'ds-form-helper-text ds-form-helper-text--inverse',
    );
  });
});
