import React from 'react';
import { shallow } from 'enzyme';
import FormLabel from './form-label';

describe('<FormLabel />', () => {
  it('should render the correct default props', () => {
    const component = shallow(<FormLabel text='Form label' />);
    expect(component.find('label').props().children).toEqual([
      <span>Form label</span>,
      null,
      null,
    ]);
    expect(component.find('label').props().htmlFor).toEqual(null);
  });

  it('should render the text', () => {
    const component = shallow(<FormLabel text='Form label' />);
    expect(component.find('label').text()).toEqual('Form label');
  });

  it('should render inverse classname if inverse prop is passed', () => {
    const component = shallow(<FormLabel inverse text='Form label' />);
    expect(component.find('label').props().className).toEqual(
      'ds-form-label ds-form-label--inverse',
    );
  });

  it('should render children', () => {
    const input = <input type='text' />;
    const component = shallow(<FormLabel text='Form label'>{input}</FormLabel>);
    expect(component.find('label').find('input').length).toEqual(1);
  });

  it('should render a tag', () => {
    const component = shallow(<FormLabel text='Form label' tag='Form tag' />);
    expect(component.find('.ds-form-tag').text()).toEqual('Form tag');
  });
});
