import React from 'react';
import { shallow } from 'enzyme';
import FormNotice from './form-notice';

describe('<FormNotice />', () => {
  it('should render the correct default class', () => {
    const component = shallow(<FormNotice text='Notice' />);
    expect(component.find('span').props().className).toEqual('ds-form-notice');
  });

  it('should render the text', () => {
    const component = shallow(<FormNotice text='Notice' />);
    expect(component.find('span').text()).toEqual('Notice');
  });

  it('should render inverse classname if inverse prop is passed', () => {
    const component = shallow(<FormNotice inverse text='Notice' />);
    expect(component.find('span').props().className).toEqual(
      'ds-form-notice ds-form-notice--inverse',
    );
  });
});
