/* eslint-disable no-magic-numbers */
import React from 'react';
import { shallow } from 'enzyme';
import FormPasswordStrengthMeter from './form-password-strength-meter';

describe('<FormPasswordStrengthMeter />', () => {
  it('should pass default props to the meter component', () => {
    const component = shallow(<FormPasswordStrengthMeter />);
    const meter = component.find('meter');
    expect(meter.props().low).toEqual(8);
    expect(meter.props().high).toEqual(12);
    expect(meter.props().max).toEqual(13);
    expect(meter.props().optimum).toEqual(13);
    expect(meter.props().value).toEqual(0);
  });

  it('should pass props to a meter component', () => {
    const component = shallow(
      <FormPasswordStrengthMeter
        label='Test'
        low={18}
        high={22}
        max={23}
        optimum={24}
        value={10}
      />,
    );
    const meter = component.find('meter');
    expect(meter.props().low).toEqual(18);
    expect(meter.props().high).toEqual(22);
    expect(meter.props().max).toEqual(23);
    expect(meter.props().optimum).toEqual(24);
    expect(meter.props().value).toEqual(10);
  });

  it('should render the label', () => {
    const component = shallow(<FormPasswordStrengthMeter label='Test' />);
    expect(component.find('span').text()).toEqual('Test');
  });
});
