import React from 'react';
import PropTypes from 'prop-types';
import classnames from 'classnames';
import 'reset-css/reset.css';

import { IDENTS_COLUMNS } from '../assets/idents-columns';

const IdentColumns = ({ className, height, ident, width }) => {
  const Svg = IDENTS_COLUMNS[ident];
  return (
    <span
      className={classnames(
        'ds-ident-columns',
        `ds-ident-columns--${ident}`,
        className,
      )}
    >
      <Svg width={width} height={height} className='ds-ident-columns__svg' />
    </span>
  );
};

IdentColumns.propTypes = {
  className: PropTypes.string,
  height: PropTypes.number,
  width: PropTypes.number,
  ident: PropTypes.oneOf([
    'bagehots-notebook',
    'bartlebys-notebook',
    'buttonwoods-notebook',
    'charlemagnes-notebook',
    'democracy-in-america',
    'erasmus',
    'free-exchange',
    'game-theory',
    'graphic-detail',
    'gulliver',
    'kaffeeklatsch',
    'open-future-2018',
    'open-future-2019',
    'prospero',
    'the-economist-explains',
  ]).isRequired,
};

IdentColumns.defaultProps = {
  className: '',
  height: 112,
  width: 112,
};

export default IdentColumns;
