import React from 'react';
import { shallow } from 'enzyme';
import IdentColumns from './ident-columns';

describe('<IdentColumns />', () => {
  it('should render the correct default props', () => {
    const component = shallow(<IdentColumns ident='bagehots-notebook' />);
    expect(component.props().className).toEqual(
      'ds-ident-columns ds-ident-columns--bagehots-notebook',
    );
  });

  it('should render the inline SVG component', () => {
    const component = shallow(<IdentColumns ident='bagehots-notebook' />);
    expect(component.find('.ds-ident-columns__svg').exists()).toBe(true);
  });

  it('should render the className prop if passed in', () => {
    const component = shallow(
      <IdentColumns ident='bagehots-notebook' className='test-classname' />,
    );
    expect(component.props().className).toContain('test-classname');
  });
});
