import React from 'react';
import PropTypes from 'prop-types';
import classnames from 'classnames';
import 'reset-css/reset.css';

import { IDENTS_NEWSLETTERS } from '../assets/idents-newsletters';

const IdentNewsletters = ({ className, height, ident, width }) => {
  const Svg = IDENTS_NEWSLETTERS[ident];
  return (
    <span
      className={classnames(
        'ds-ident-newsletters',
        `ds-ident-newsletters--${ident}`,
        className,
      )}
    >
      <Svg
        width={width}
        height={height}
        className='ds-ident-newsletters__svg'
      />
    </span>
  );
};

IdentNewsletters.propTypes = {
  className: PropTypes.string,
  height: PropTypes.number,
  width: PropTypes.number,
  ident: PropTypes.oneOf([
    'checks-and-balance',
    'cover-story',
    'plot-twist',
    'drum-tower',
    'espresso',
    'money-talks',
    'off-the-charts',
    'simply-science',
    'the-bottom-line',
    'the-climate-issue',
    'the-economist-this-week',
    'the-economist-today',
    'the-extraordinary-story',
    'the-world-in-brief',
    'blighty',
  ]).isRequired,
};

IdentNewsletters.defaultProps = {
  className: '',
  height: 112,
  width: 112,
};

export default IdentNewsletters;
