import React from 'react';
import PropTypes from 'prop-types';
import classnames from 'classnames';
import 'reset-css/reset.css';

import { IDENTS_PODCASTS } from '../assets/idents-podcasts';

const IdentPodcasts = ({ className, height, ident, width }) => {
  const Svg = IDENTS_PODCASTS[ident];
  return (
    <span
      className={classnames(
        'ds-ident-podcasts',
        `ds-ident-podcasts--${ident}`,
        className,
      )}
    >
      <Svg width={width} height={height} className='ds-ident-podcasts__svg' />
    </span>
  );
};

IdentPodcasts.propTypes = {
  className: PropTypes.string,
  height: PropTypes.number,
  width: PropTypes.number,
  ident: PropTypes.oneOf([
    'babbage',
    'checks-and-balance',
    'editors-picks',
    'money-talks',
    'the-economist-asks',
    'the-intelligence',
    'the-jab',
    'the-world-ahead',
  ]).isRequired,
};

IdentPodcasts.defaultProps = {
  className: '',
  height: 112,
  width: 112,
};

export default IdentPodcasts;
