import React from 'react';
import { shallow } from 'enzyme';
import IdentPodcasts from './ident-podcasts';

describe('<IdentPodcasts />', () => {
  it('should render the correct default props', () => {
    const component = shallow(<IdentPodcasts ident='the-jab' />);
    expect(component.props().className).toEqual(
      'ds-ident-podcasts ds-ident-podcasts--the-jab',
    );
  });

  it('should render the inline SVG component', () => {
    const component = shallow(<IdentPodcasts ident='the-jab' />);
    expect(component.find('.ds-ident-podcasts__svg').exists()).toBe(true);
  });

  it('should render the className prop if passed in', () => {
    const component = shallow(
      <IdentPodcasts ident='the-jab' className='test-classname' />,
    );
    expect(component.props().className).toContain('test-classname');
  });
});
