import React from 'react';
import { shallow } from 'enzyme';
import Indicator from './indicator';

describe('<Indicator />', () => {
  it('should render inverse classname if inverse prop is passed', () => {
    const component = shallow(<Indicator inverse />);
    expect(component.find('circle').props().className).toEqual(
      'ds-indicator__circle ds-indicator__circle--inverse',
    );
  });
});
