import React from 'react';
import { shallow } from 'enzyme';
import InlineAlert from './inline-alert';

describe('<InlineAlert />', () => {
  const text = 'Inline alert text';
  const description = 'Inline alert description';
  it('should render the correct default className', () => {
    const component = shallow(<InlineAlert />);
    expect(component.find('div').props().className).toEqual('ds-inline-alert');
  });

  it('should render the text it is passed', () => {
    const component = shallow(
      <InlineAlert description={description} text={text} />,
    );
    expect(component.text()).toEqual(text + description);
  });

  it('should render the success classname if passed success as a prop', () => {
    const component = shallow(
      <InlineAlert description={description} text={text} variant='success' />,
    );
    expect(component.find('div').props().className).toEqual(
      'ds-inline-alert ds-inline-alert--success',
    );
  });

  it('should render the error classname if passed success as a prop', () => {
    const component = shallow(
      <InlineAlert description={description} text={text} variant='error' />,
    );
    expect(component.find('div').props().className).toEqual(
      'ds-inline-alert ds-inline-alert--error',
    );
  });

  it('should render the warning classname if passed warning as a prop', () => {
    const component = shallow(
      <InlineAlert description={description} text={text} variant='warning' />,
    );
    expect(component.find('div').props().className).toEqual(
      'ds-inline-alert ds-inline-alert--warning',
    );
  });
});
