import { clientAdminData } from '../../constants';

/**
 * Retrieve the client admin my group subscription link name and url
 * @param {string} [myGroupSubscriptionName] - Overriding my group subscription link name
 * @param {string} [myGroupSubscriptionUrl] - Overriding my group subscription link url
 * @returns {array} My group subscription link name and url
 */
const getClientAdminData = (
  myGroupSubscriptionName,
  myGroupSubscriptionUrl,
) => {
  const processedClientAdminData = { ...clientAdminData };

  if (myGroupSubscriptionName) {
    processedClientAdminData.name = myGroupSubscriptionName;
  }

  if (myGroupSubscriptionUrl) {
    processedClientAdminData.url = myGroupSubscriptionUrl;
  }

  return [processedClientAdminData];
};

export default getClientAdminData;
