import React from 'react';
import { shallow } from 'enzyme';
import MastheadLinks from './masthead-links';

describe('<MastheadLinks />', () => {
  it('should render two navigation links', () => {
    const component = shallow(<MastheadLinks />);
    expect(component.find('NavigationLink').length).toEqual(2);
  });

  it('should apply the inverse styles when passed inverse as a prop', () => {
    const component = shallow(<MastheadLinks inverse />);
    expect(
      component
        .find('NavigationLink')
        .first()
        .props().inverse,
    ).toEqual(true);
  });

  it('should apply selected link to weekly edition if passed as a prop', () => {
    const component = shallow(<MastheadLinks selectedLink='weekly-edition' />);
    expect(
      component
        .find('NavigationLink')
        .at(0)
        .props().selected,
    ).toEqual(true);
  });
});
