import React from 'react';
import PropTypes from 'prop-types';
import 'reset-css/reset.css';
import '../color/color';
import '../media-queries/media-queries';
import css from './masthead-logo.css';
import { urls } from '../../constants';
import Logo from '../assets/logo/logo';
import MonogramSquare from '../assets/monogram/monogram-square';

const MastheadLogo = ({ href }) => {
  return (
    // Which logo gets displayed is controlled by media queries in the CSS so we don't get layout shifts
    <a href={href} className='ds-masthead-home'>
      <Logo className={css['ds-masthead-logo']} />
      <MonogramSquare className={css['ds-masthead-monogram']} />
    </a>
  );
};

MastheadLogo.defaultProps = {
  href: urls.economist,
};

MastheadLogo.propTypes = {
  href: PropTypes.string,
};

export default MastheadLogo;
