import React from 'react';
import { shallow } from 'enzyme';
import MastheadSecondaryLinks from './masthead-secondary-links';

describe('<MastheadSecondaryLinks />', () => {
  const links = [
    { text: 'Test', href: '/test1', id: '1' },
    { text: 'Test2', href: '/test2', id: '2' },
  ];

  it('should render two navigation links', () => {
    const component = shallow(<MastheadSecondaryLinks links={links} />);
    expect(component.find('NavigationLink').length).toEqual(2);
  });

  it('should apply the inverse styles when passed inverse as a prop', () => {
    const component = shallow(<MastheadSecondaryLinks links={links} inverse />);
    expect(
      component
        .find('NavigationLink')
        .first()
        .props().inverse,
    ).toEqual(true);
  });
});
