import React from 'react';
import { shallow } from 'enzyme';
import FormFieldSearch from '../forms/form-field-search';
import SearchForm from './search-form';

describe('<SearchForm />', () => {
  it('should pass default props to the Search component', () => {
    const component = shallow(<SearchForm />);
    const combinedField = component.find(FormFieldSearch);
    expect(combinedField.props().labelText).toEqual('Search');
  });

  it('should pass props to a Search component', () => {
    const component = shallow(<SearchForm labelText='Form label' />);
    const searchProps = component.find(FormFieldSearch).props();
    expect(searchProps.labelText).toEqual('Form label');
  });
});
