import React from 'react';
import PropTypes from 'prop-types';
import classnames from 'classnames';
import 'reset-css/reset.css';
import '../color/color';
import '../interactions/interactions';
import '../typography/typography';
import css from './menu-disclosure.css';

const MenuDisclosure = ({
  testId,
  menuIsOpen,
  inverse,
  className,
  hideMenuText,
  ...otherProps
}) => (
  <a
    className={classnames(className, css['ds-menu-disclosure'], {
      [css['ds-menu-disclosure--inverse']]: menuIsOpen,
      [css['is-open']]: menuIsOpen,
      [css['ds-menu-disclosure--inverse']]: inverse,
      [css['ds-menu-disclosure--no-menu-text']]: hideMenuText,
    })}
    href='#'
    data-menu-is-open={menuIsOpen}
    aria-expanded={menuIsOpen}
    id='menu-button'
    data-test-id={testId}
    {...otherProps}
  >
    {menuIsOpen ? (
      <svg viewBox='0 0 24 24' xmlns='http://www.w3.org/2000/svg'>
        <g id='icon-menu-disclosure' fill='none' fillRule='evenodd'>
          <path
            className='path-foreground'
            fill='#0D0D0D'
            d='M18.75 6.61l-1.36-1.36L12 10.64 6.61 5.25 5.25 6.61 10.64 12l-5.39 5.39 1.36 1.36L12 13.36l5.39 5.39 1.36-1.36L13.36 12z'
          />
        </g>
      </svg>
    ) : (
      <svg viewBox='0 0 24 24' xmlns='http://www.w3.org/2000/svg'>
        <g id='icon-menu-disclosure' fill='none' fillRule='evenodd'>
          <path d='M0 0h24v24H0z' />
          <path
            className='path-foreground'
            d='M3 18h18v-2H3v2zm0-5h18v-2H3v2zm0-7v2h18V6H3z'
            fill='#0D0D0D'
            fillRule='nonzero'
          />
        </g>
      </svg>
    )}
    {menuIsOpen ? 'Close' : 'Menu'}
  </a>
);

MenuDisclosure.defaultProps = {
  testId: 'Menu link',
  className: null,
  menuIsOpen: false,
  hideMenuText: false,
  inverse: false,
};

MenuDisclosure.propTypes = {
  testId: PropTypes.string,
  className: PropTypes.string,
  menuIsOpen: PropTypes.bool,
  hideMenuText: PropTypes.bool,
  inverse: PropTypes.bool,
};

export default MenuDisclosure;
