import React from 'react';
import { shallow } from 'enzyme';
import MenuDisclosure from './menu-disclosure';

describe('<MenuDisclosure />', () => {
  it('should render menu disclosure', () => {
    const component = shallow(<MenuDisclosure />);
    expect(component.prop('className')).toEqual('ds-menu-disclosure');
  });

  it('should render a test id when one is given', () => {
    const component = shallow(<MenuDisclosure testId='Menu Disclosure' />);
    expect(component.prop('data-test-id')).toEqual('Menu Disclosure');
  });

  it('should say Menu when menu is closed', () => {
    const component = shallow(<MenuDisclosure testId='Menu Disclosure' />);
    expect(component.text()).toEqual('Menu');
  });

  it('should say Close when menu is open', () => {
    const component = shallow(
      <MenuDisclosure testId='Menu Disclosure' menuIsOpen />,
    );
    expect(component.text()).toEqual('Close');
  });
});
