import React from 'react';
import { shallow } from 'enzyme';
import NavigationDisclosure from './navigation-disclosure';
import NavigationDisclosureIcon from './navigation-disclosure-icon';

describe('NavigationDisclosureIcon', () => {
  const url = 'www.test.com';

  it('should render children', () => {
    const component = shallow(<NavigationDisclosure text='Text' href={url} />);
    expect(component.text()).toEqual('Text');
  });

  it('should add account icon className when account icon is set', () => {
    const component = shallow(
      <NavigationDisclosureIcon
        icon='account'
        href={url}
        text='example text'
      />,
    );
    expect(component.hasClass('ds-navigation-disclosure--icon')).toBe(true);
    expect(component.hasClass('ds-navigation-disclosure--icon-account')).toBe(
      true,
    );
  });

  it('should add search icon className when search icon is set', () => {
    const component = shallow(
      <NavigationDisclosureIcon icon='search' href={url} text='example text' />,
    );
    expect(component.hasClass('ds-navigation-disclosure--icon')).toBe(true);
    expect(component.hasClass('ds-navigation-disclosure--icon-search')).toBe(
      true,
    );
  });
});
