import React from 'react';
import { shallow } from 'enzyme';
import NavigationLink from './navigation-link';
import NavigationLinkIcon from './navigation-link-icon';

describe('NavigationLinkIcon', () => {
  const url = 'www.test.com';

  it('should render children', () => {
    const component = shallow(<NavigationLink text='Text' href={url} />);
    expect(component.text()).toEqual('Text');
  });

  it('should add account icon className when account icon is set', () => {
    const component = shallow(
      <NavigationLinkIcon icon='account' href={url} text='example text' />,
    );
    expect(component.hasClass('ds-navigation-link--icon')).toBe(true);
    expect(component.hasClass('ds-navigation-link--icon-account')).toBe(true);
  });

  it('should add search icon className when search icon is set', () => {
    const component = shallow(
      <NavigationLinkIcon icon='search' href={url} text='example text' />,
    );
    expect(component.hasClass('ds-navigation-link--icon')).toBe(true);
    expect(component.hasClass('ds-navigation-link--icon-search')).toBe(true);
  });
});
