/* eslint-disable no-magic-numbers */
import generatePagination from './generatePagination';

describe('generatePagination', () => {
  it('should generate correct pagination', () => {
    const page = (value, active = false) => ({
      id: `page-no-${value}`,
      value,
      active,
      disabled: false,
    });
    const ellipsis = index => ({
      id: `ellipsis-${index}`,
      value: '...',
      active: false,
      disabled: true,
    });

    expect(generatePagination(10, 50)).toEqual([
      page(1),
      ellipsis(1),
      page(8),
      page(9),
      page(10, true),
      page(11),
      page(12),
      ellipsis(7),
      page(50),
    ]);
    expect(generatePagination(50, 50)).toEqual([
      page(1),
      ellipsis(1),
      page(48),
      page(49),
      page(50, true),
    ]);
    expect(generatePagination(49, 50)).toEqual([
      page(1),
      ellipsis(1),
      page(47),
      page(48),
      page(49, true),
      page(50),
    ]);
    expect(generatePagination(45, 50)).toEqual([
      page(1),
      ellipsis(1),
      page(43),
      page(44),
      page(45, true),
      page(46),
      page(47),
      ellipsis(7),
      page(50),
    ]);
    expect(generatePagination(30, 50)).toEqual([
      page(1),
      ellipsis(1),
      page(28),
      page(29),
      page(30, true),
      page(31),
      page(32),
      ellipsis(7),
      page(50),
    ]);
    expect(generatePagination(6, 50)).toEqual([
      page(1),
      ellipsis(1),
      page(4),
      page(5),
      page(6, true),
      page(7),
      page(8),
      ellipsis(7),
      page(50),
    ]);
    expect(generatePagination(5, 50)).toEqual([
      page(1),
      ellipsis(1),
      page(3),
      page(4),
      page(5, true),
      page(6),
      page(7),
      ellipsis(7),
      page(50),
    ]);
    expect(generatePagination(4, 50)).toEqual([
      page(1),
      page(2),
      page(3),
      page(4, true),
      page(5),
      page(6),
      ellipsis(6),
      page(50),
    ]);
    expect(generatePagination(3, 50)).toEqual([
      page(1),
      page(2),
      page(3, true),
      page(4),
      page(5),
      ellipsis(5),
      page(50),
    ]);
    expect(generatePagination(2, 50)).toEqual([
      page(1),
      page(2, true),
      page(3),
      page(4),
      ellipsis(4),
      page(50),
    ]);
    expect(generatePagination(1, 50)).toEqual([
      page(1, true),
      page(2),
      page(3),
      ellipsis(3),
      page(50),
    ]);
  });
});
