import React from 'react';
import { shallow } from 'enzyme';
import SectionLink from './section-link';

describe('<SectionLink />', () => {
  const title = 'Section link';
  const link = 'www.test.com';
  const component = shallow(<SectionLink title={title} href={link} />);

  it('should render the title', () => {
    expect(component.text()).toEqual(title);
  });

  it('should add the correct link', () => {
    expect(component.props().href).toEqual(link);
  });
});
