import React from 'react';
import { shallow } from 'enzyme';
import ShareLink from './share-link';

describe('<ShareIconLink />', () => {
  const url = 'www.test.com';
  const icon = <div />;
  const element = shallow(<ShareLink href={url} icon={icon} />);

  it('should link to the correct url', () => {
    expect(element.props().href).toEqual(url);
  });

  it('should render the icon', () => {
    expect(element.childAt(0).type()).toEqual('div');
  });

  it('should render inverse classname if inverse prop is passed', () => {
    const component = shallow(<ShareLink inverse href={url} icon={icon} />);
    expect(component.find('a').props().className).toEqual(
      'ds-share-link ds-share-link--inverse',
    );
  });
});
