import {
  SocialIconFacebook,
  SocialIconTwitter,
  SocialIconLinkedIn,
  SocialIconWhatsApp,
} from '../assets/assets';

import generateShareIcon from './generate-share-icon';

describe('generateShareIcon', () => {
  it('should return the matching icon for each platform passed', () => {
    expect(generateShareIcon('Facebook')).toEqual(SocialIconFacebook);
    expect(generateShareIcon('Twitter')).toEqual(SocialIconTwitter);
    expect(generateShareIcon('LinkedIn')).toEqual(SocialIconLinkedIn);
    expect(generateShareIcon('WhatsApp')).toEqual(SocialIconWhatsApp);
  });
});
