import React from 'react';
import PropTypes from 'prop-types';
import classnames from 'classnames';
import 'reset-css/reset.css';
import '../color/color';
import '../grid/grid';
import '../grid/layout-grid';
import '../typography/typography';
import css from './slim-media-promo.css';
import LinkWithArrowIcon from '../link-with-arrow-icon/link-with-arrow-icon';

const SlimMediaPromo = ({ title, inverse, variant, linkText, linkURL }) => (
  <div
    className={classnames(css['ds-slim-media-promo'], {
      [css['ds-slim-media-promo--inverse']]: inverse,
      [css[`ds-slim-media-promo--${variant}`]]: variant,
    })}
  >
    <div
      className={`ds-layout-grid ds-layout-grid--edged ${
        css['ds-layout-slim-media-promo']
      }`}
    >
      <h2 className={css['ds-slim-media-promo__title']}>{title}</h2>
      <LinkWithArrowIcon
        href={linkURL}
        text={linkText}
        inverse={inverse}
        heavy
        minor
      />
    </div>
  </div>
);

SlimMediaPromo.defaultProps = {
  inverse: false,
  variant: null,
};

SlimMediaPromo.propTypes = {
  title: PropTypes.string.isRequired,
  linkText: PropTypes.string.isRequired,
  linkURL: PropTypes.string.isRequired,
  variant: PropTypes.oneOf(['singapore-55', 'paris-90', 'chicago-20']),
  inverse: PropTypes.bool,
};

export default SlimMediaPromo;
