import React from 'react';
import PropTypes from 'prop-types';
import 'reset-css/reset.css';
import '../color/color';
import '../interactions/interactions';
import '../grid/grid';
import '../typography/typography';
import css from './stage-banner.css';

const StageBanner = ({ badge, text }) => (
  <div className={css['ds-stage-banner']}>
    <div className={css['ds-layout-stage-banner']}>
      {badge && <span className={css['ds-stage-banner__badge']}>{badge}</span>}
      <p className={css['ds-stage-banner__text']}>{text}</p>
    </div>
  </div>
);

StageBanner.defaultProps = {
  badge: '',
  text: '',
};

StageBanner.propTypes = {
  badge: PropTypes.string,
  text: PropTypes.oneOfType([PropTypes.node, PropTypes.string]),
};

export default StageBanner;
