import React from 'react';
import { shallow } from 'enzyme';
import StageBanner from './stage-banner';

describe('<StageBanner />', () => {
  const badgeText = 'Stage banner badge text';
  const text = 'Stage banner text';

  it('should render the correct default className', () => {
    const component = shallow(<StageBanner badge={badgeText} text={text} />);
    expect(
      component
        .find('div')
        .first()
        .props().className,
    ).toEqual('ds-stage-banner');
  });

  it('should render the badgeText', () => {
    const component = shallow(<StageBanner badge={badgeText} text={text} />);

    expect(component.find('.ds-stage-banner__badge').text()).toContain(
      badgeText,
    );
  });

  it('should render the text', () => {
    const component = shallow(<StageBanner badge={badgeText} text={text} />);

    expect(component.find('.ds-stage-banner__text').text()).toContain(text);
  });
});
