import React from 'react';
import { shallow } from 'enzyme';
import StatusAlert from './status-alert';

describe('<StatusAlert />', () => {
  const text = 'Status alert text';
  it('should render the correct default className', () => {
    const component = shallow(<StatusAlert />);
    expect(component.find('div').props().className).toEqual('ds-status-alert');
  });

  it('should render the text it is passed', () => {
    const component = shallow(<StatusAlert text={text} />);
    expect(component.text()).toEqual(text);
  });

  it('should render the success classname if passed success as a prop', () => {
    const component = shallow(<StatusAlert text={text} variant='success' />);
    expect(component.find('div').props().className).toEqual(
      'ds-status-alert ds-status-alert--success',
    );
  });

  it('should render the error classname if passed success as a prop', () => {
    const component = shallow(<StatusAlert text={text} variant='error' />);
    expect(component.find('div').props().className).toEqual(
      'ds-status-alert ds-status-alert--error',
    );
  });

  it('should render the warning classname if passed warning as a prop', () => {
    const component = shallow(<StatusAlert text={text} variant='warning' />);
    expect(component.find('div').props().className).toEqual(
      'ds-status-alert ds-status-alert--warning',
    );
  });
});
