import React from 'react';
import { shallow } from 'enzyme';
import TableCellCollapse from './table-cell-collapse';

const child = <div />;
const element = shallow(
  <TableCellCollapse dataLabel='table header'>{child}</TableCellCollapse>,
);

describe('<TableCellCollapse />', () => {
  it('should render a td element', () => {
    expect(element.find('td').length).toEqual(1);
  });

  it('should render the children', () => {
    expect(
      element
        .find('td')
        .childAt(0)
        .type(),
    ).toEqual('div');
  });

  it('should pass dataLabel prop to the data-label attribute', () => {
    expect(element.find('td').props()['data-label']).toEqual('table header');
  });
});
