import React from 'react';
import { shallow } from 'enzyme';
import TableCollapse from './table-collapse';

const child = <div />;

describe('<TableCollapse />', () => {
  it('should render a Table component', () => {
    const element = shallow(<TableCollapse />);

    expect(element.find('Table').length).toEqual(1);
  });

  it('should render the children', () => {
    const element = shallow(<TableCollapse>{child}</TableCollapse>);
    expect(
      element
        .find('Table')
        .childAt(0)
        .type(),
    ).toEqual('div');
  });
});
