import React from 'react';
import { shallow } from 'enzyme';
import Table from './table';

const child = <div />;

describe('<Table />', () => {
  it('should render default classname', () => {
    const element = shallow(<Table />);
    expect(element.find('table').props().className).toEqual('ds-table');
  });

  it('should render the children', () => {
    const element = shallow(<Table>{child}</Table>);
    expect(
      element
        .find('table')
        .childAt(0)
        .type(),
    ).toEqual('div');
  });

  it('should render scroll classname if scroll prop is passed', () => {
    const element = shallow(<Table scroll />);
    expect(element.props().className).toEqual('ds-table--scroll');
  });
});
