import React from 'react';
import { shallow } from 'enzyme';
import TabItem from './tab-item';

describe('<TabItem />', () => {
  it('should render the correct default props', () => {
    const component = shallow(<TabItem />);
    expect(component.props().className).toEqual('ds-tab-item');
    expect(component.find('a').props()['aria-selected']).toBe(false);
    expect(component.find('a').props().className).toEqual('ds-tab-item__link');
  });

  it('should render the title', () => {
    const component = shallow(<TabItem title='Tab item' />);
    expect(component.text()).toEqual('Tab item');
    expect(component.find('a').props().title).toEqual('Tab item');
  });

  it('should render the adjacent className when adjacent is set', () => {
    const component = shallow(<TabItem adjacent title='Tab item' />);
    expect(component.find('a').props().className).toEqual(
      'ds-tab-item__link ds-tab-item__link--adjacent',
    );
  });

  it('should render the minor className when minor is set', () => {
    const component = shallow(<TabItem minor title='Tab item' />);
    expect(component.find('a').props().className).toEqual(
      'ds-tab-item__link ds-tab-item__link--minor',
    );
  });

  it('should render the selected className when selected is set', () => {
    const component = shallow(<TabItem selected title='Tab item' />);
    expect(component.find('a').props().className).toEqual(
      'ds-tab-item__link ds-tab-item__link--selected',
    );
  });

  it('should render aria-selected as true when selected is set', () => {
    const component = shallow(<TabItem selected title='Tab item' />);
    expect(component.find('a').props()['aria-selected']).toBe(true);
  });
});
