import React from 'react';
import { shallow } from 'enzyme';
import TabList from './tab-list';

describe('<TabList />', () => {
  it('should render the children', () => {
    const component = shallow(<TabList>Test</TabList>);
    expect(component.children().text()).toEqual('Test');
  });

  it('should render the correct default props', () => {
    const component = shallow(<TabList>Test</TabList>);
    expect(component.props().className).toEqual('ds-tab-list');
  });

  it('should render the adjacent className when adjacent is set', () => {
    const component = shallow(<TabList adjacent>Test</TabList>);
    expect(component.props().className).toEqual(
      'ds-tab-list ds-tab-list--adjacent',
    );
  });
});
