import React from 'react';
import { shallow } from 'enzyme';
import TabPanel from './tab-panel';

describe('<TabPanel />', () => {
  it('should render the children', () => {
    const component = shallow(<TabPanel labelledby='tab1'>Test</TabPanel>);
    expect(component.children().text()).toEqual('Test');
  });

  it('should pass the tabId to the aria-labelledby prop', () => {
    const component = shallow(
      <TabPanel labelledby='tab1'>
        <p>tab</p>
      </TabPanel>,
    );
    expect(component.props()['aria-labelledby']).toEqual('tab1');
  });
});
