import React from 'react';
import { shallow } from 'enzyme';
import Tag from './tag';

describe('<Tag />', () => {
  const text = 'Tag text';
  it('should render the correct default className', () => {
    const component = shallow(<Tag />);
    expect(component.find('span').props().className).toEqual('ds-tag');
  });

  it('should render the text it is passed', () => {
    const component = shallow(<Tag text={text} />);
    expect(component.text()).toEqual(text);
  });
});
