const randomChar = () => Math.floor(Math.random() * 36).toString(36);

const getRandomChars = length => Array.from({ length }, randomChar).join('');

export const getSvgIds = key => {
  const hash = getRandomChars(8);
  const defs = {
    defId: `${key}_def_${hash}`,
    maskId: `${key}_mask_${hash}`,
  };
  return {
    ...defs,
    defSelector: `#${defs.defId}`,
    maskSelector: `url(#${defs.maskId})`,
  };
};

export default getSvgIds;
