import getUserInitials from './user-initials';

describe('getUserInitials', () => {
  it('should get initials for full name', () => {
    const initials = getUserInitials('John Smith');

    expect(initials).toEqual('JS');
  });

  it('should get initials for full name with middle name', () => {
    const initials = getUserInitials('John Smith Morton');

    expect(initials).toEqual('JM');
  });

  it('should get initials for incomplete full name', () => {
    const initials = getUserInitials('Smith');

    expect(initials).toEqual('S');
  });

  it('should get empty initials for missing full name', () => {
    const initials = getUserInitials('');

    expect(initials).toEqual('');
  });
});
