import React from 'react';
import { shallow } from 'enzyme';
import Link from './link';

describe('<Link />', () => {
  const url = 'www.test.com';

  it('should render children', () => {
    const component = shallow(
      <Link href={url}>
        <h5>Heading link</h5>
      </Link>,
    );
    expect(component.props().children).toEqual(<h5>Heading link</h5>);
  });

  it('should add eiu-custom-link className when type is custom', () => {
    const component = shallow(
      <Link inverse href={url} type='custom'>
        custom
      </Link>,
    );
    expect(component.props().className).toEqual('eiu-custom-link');
  });

  it('should have eiu-link className when type is set to default', () => {
    const component = shallow(
      <Link href={url} type='default'>
        default
      </Link>,
    );
    expect(component.props().className).toEqual('eiu-link');
  });
});
